import React, { useState } from 'react';
import Card from './Card';

function Services() {
  // State to manage modal visibility and content
  const [modalVisible, setModalVisible] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState({});

  // Function to handle the "Details" button click
  const handleDetailsClick = (product) => {
    setSelectedProduct(product);
    setModalVisible(true);
  };

  // Function to handle modal close
  const handleCloseModal = () => {
    setModalVisible(false);
  };

  return (
    <div className="container services">
      <h2 className="main-title text-center">Products</h2>
      <div className="card-cover">
        <div className="col-md-12">
          <div className="row">
            <div className="col-md-4 mb-2">
              <Card
                title="WShisha Briquette Charcoal"
                img="card1.png"
                text="Morbi eget neque risus. Duis erat quam, porta quis enim id, venenatis blandit nunc."
                onDetailsClick={() => handleDetailsClick({ title: "WShisha Briquette Charcoal", specifications: "Specifications for WShisha Briquette Charcoal" })}
              />
            </div>
            <div className="col-md-4 mb-2">
              <Card
                title="Barbeque Charcoal"
                img="card2.png"
                text="Maecenas dictum efficitur felis non gravida. Vestibulum vitae ante luctus, accumsan mi vitae, pretium metus."
                onDetailsClick={() => handleDetailsClick({ title: "Barbeque Charcoal", specifications: "Specifications for Barbeque Charcoal" })}
              />
            </div>
            <div className="col-md-4 mb-2">
              <Card
                title="Hardwood Lump Charcoal"
                img="card3.png"
                text="Phasellus suscipit nibh at nisi finibus vestibulum sit amet vitae massa. Suspendisse non est eget elit pulvinar consectetur nec non sapien."
                onDetailsClick={() => handleDetailsClick({ title: "Hardwood Lump Charcoal", specifications: "Specifications for Hardwood Lump Charcoal" })}
              />
            </div>
          </div>
        </div>
      </div>

      {/* Modal for displaying product details */}
      {modalVisible && (
        <div className="modal fade show" tabIndex="-1" role="dialog" style={{ display: 'block' }}>
          <div className="modal-dialog" role="document">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">{selectedProduct.title} Details</h5>
                <button type="button" className="close" data-dismiss="modal" aria-label="Close" onClick={handleCloseModal}>
                  <span aria-hidden="true">&times;</span>
                </button>
              </div>
              <div className="modal-body">
                <p>{selectedProduct.specifications}</p>
              </div>
              <div className="modal-footer">
                <button type="button" className="btn btn-secondary" onClick={handleCloseModal}>
                  Close
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Services;
