import React, { useState } from 'react';
import AlertDialogSlide from './AlertDialogSlide'; // Import the AlertDialogSlide component

function Card(props) {
  const [showDialog, setShowDialog] = useState(false);

  const handleDetailClick = () => {
    setShowDialog(true);
  };

  const handleCloseDialog = () => {
    setShowDialog(false);
  };

  return (
    <div className="card">
      <br />

      <div className="text-center">
        <img alt="card-img" src={"./img/" + props.img} className="text-center img-fluid" />
      </div>
      <div className="text-center">
        <h3 className="card-title">{props.title}</h3>
      </div>
      <div className="p-3 text-center">
        <p className="card-text">{props.text}</p>
        <button
          className="btn btn-primary"
          style={{ cursor: "pointer" }}
          onClick={handleDetailClick}
        >
          Detail
        </button>

        {/* Render the Material-UI AlertDialogSlide component */}
        <AlertDialogSlide open={showDialog} handleClose={handleCloseDialog} />
      </div>
    </div>
  );
}

export default Card;
