import { Link } from "react-scroll";

function Header() {
  function openBar() {
    const bar = document.querySelector(".bar");
    bar.classList.toggle("opened");
  }

  return (
    <header>
      <div className="video-container">
        <iframe
          width="560"
          height="315"
          src="https://www.youtube.com/embed/qvmDOWBIPas"
          title="YouTube video player"
          frameborder="0"
          allowfullscreen
        ></iframe>
      </div>
      <h1>Open Source Website</h1>
      <div className="row">
        {/* Scroll to "about-scroll" section */}
        <Link
          onClick={openBar}
          to="about-scroll"
          spy={true}
          smooth={true}
          duration={1000}
          activeClass="active"
        >
          <button className="btn" style={{ cursor: "pointer" }}>
            Product
          </button>
        </Link>

        {/* Open WhatsApp chat using the provided link */}
        <a
          href="https://wa.me/message/DDFS53IPYRVRE1"
          target="_blank" // Open link in a new tab
          rel="noopener noreferrer" // Security best practice for target="_blank" links
        >
          <button className="btn" style={{ cursor: "pointer" }}>
            Contact us
          </button>
        </a>
      </div>

      <div className="headerbg"></div>
    </header>
  );
}

export default Header;
