import React, { useState } from 'react';
import AlertDialogSlide from './AlertDialogSlide'; // Import the AlertDialogSlide component
import './Card.css'; // Import your custom CSS file for styling if needed

const Card = ({ img, title, text }) => {
  const [showDialog, setShowDialog] = useState(false);

  const handleDetailClick = () => {
    setShowDialog(true);
  };

  const handleCloseDialog = () => {
    setShowDialog(false);
  };

  return (
    <div className="card">
      <br />
      <div className="text-center">
        <img alt="card-img" src={`./img/${img}`} className="text-center img-fluid" />
      </div>
      <div className="text-center">
        <h3 className="card-title">{title}</h3>
      </div>
      <div className="p-3 text-center">
        <p className="card-text">{text}</p>
        <button
          className="btn btn-primary card-button"
          onClick={handleDetailClick}
        >
          Detail
        </button>

        {/* Render the Material-UI AlertDialogSlide component */}
        <AlertDialogSlide open={showDialog} handleClose={handleCloseDialog} />
      </div>
    </div>
  );
};

export default Card;
